import React from 'react';
import {Inline} from '@shopify/polaris';

import {withPolarisExample} from '../../src/components/PolarisExampleWrapper';

function InlineWithNonWrappingExample() {
return (
<Inline gap="025" wrap={false}>
<Placeholder width="106px" height="36px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
</Inline>
);
}

const Placeholder = ({height = 'auto', width = 'auto'}) => {
return (
<div
style={{
background: '#20828D',
height: height,
width: width,
}}
/>
);
};

export default withPolarisExample(InlineWithNonWrappingExample);

import React from 'react';
import {Inline, AlphaStack} from '@shopify/polaris';

import {withPolarisExample} from '../../src/components/PolarisExampleWrapper';

function InlineWithGapExample() {
return (
<AlphaStack>
<Inline>
<SpacingBackground width="436px" height="20px" margin>
<Inline gap="4" wrap={false} blockAlign="center">
<Placeholder width="106px" height="36px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
</Inline>
</SpacingBackground>
</Inline>
<SpacingBackground width="227.98px" height="40px">
<Inline gap="4" wrap={false} blockAlign="end">
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
</Inline>
</SpacingBackground>
</AlphaStack>
);
}

const SpacingBackground = ({
children,
width,
height,
margin = false,
}: {
children: React.ReactNode;
width: string;
height: string;
margin?: boolean;
}) => {
return (
<div
style={{
display: 'flex',
background: '#E0F8EE',
width: width,
height: height,
marginBlockEnd: margin ? '8px' : 'none',
}}
>
{children}
</div>
);
};

const Placeholder = ({height = 'auto', width = 'auto'}) => {
return (
<div
style={{
background: '#20828D',
height: height,
width: width,
}}
/>
);
};

export default withPolarisExample(InlineWithGapExample);

import React from 'react';
import {AlphaStack, Inline, Text, Page, Divider} from '@shopify/polaris';

import {withPolarisExample} from '../../src/components/PolarisExampleWrapper';

function InlineWithAlignExample() {
return (
<Page narrowWidth>
<AlphaStack gap="16">
<Inline gap="025" align="start">
<Placeholder width="106px" label="Start" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
</Inline>
<Divider />
<Inline gap="025" align="center">
<Placeholder width="106px" label="Center" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
</Inline>
<Divider />
<Inline gap="025" align="end">
<Placeholder width="106px" label="End" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
<Placeholder width="106px" height="20px" />
</Inline>
</AlphaStack>
</Page>
);
}

const Placeholder = ({
label = '',
height = 'auto',
width = 'auto',
showBorder = false,
}) => {
return (
<div
style={{
padding: '6px 0',
background: '#20828D',
height: height,
width: width,
borderInlineStart: showBorder ? '1px dashed #EAFAF3' : 'none',
}}
>
<Inline align="center">
<div
style={{
color: '#FFFFFF',
}}
>
<Text as="h2" variant="bodyMd" fontWeight="medium">
{label}
</Text>
</div>
</Inline>
</div>
);
};

export default withPolarisExample(InlineWithAlignExample);
